contracts/Arbitration.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./libraries/Counters.sol";

/**
 * @title Arbitration
 * @dev Manages dispute resolution and arbitrator management
 * @notice Handles arbitrator registration, dispute assignment, and resolution tracking
 */
contract Arbitration is AccessControl, ReentrancyGuard, Pausable {
    using Counters for Counters.Counter;

    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    // State variables
    Counters.Counter private _disputeIdCounter;
    Counters.Counter private _arbitratorIdCounter;

    // Arbitration fees
    uint256 public constant ARBITRATOR_FEE_BPS = 100; // 1% of dispute amount
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MIN_DISPUTE_AMOUNT = 0.01 ether; // Minimum dispute amount

    // Events
    event ArbitratorRegistered(
        uint256 indexed arbitratorId,
        address indexed arbitrator,
        string name,
        string description,
        uint256 fee
    );
    
    event ArbitratorRemoved(
        uint256 indexed arbitratorId,
        address indexed arbitrator
    );
    
    event DisputeAssigned(
        uint256 indexed disputeId,
        uint256 indexed rentalId,
        address indexed arbitrator,
        uint256 deadline
    );
    
    event DisputeResolved(
        uint256 indexed disputeId,
        address indexed arbitrator,
        bool resolved,
        string resolution
    );
    
    event ArbitratorRated(
        uint256 indexed arbitratorId,
        address indexed rater,
        uint256 rating,
        string feedback
    );

    // Structs
    enum DisputeStatus {
        Pending,    // Dispute created, awaiting arbitrator assignment
        Assigned,   // Arbitrator assigned
        InProgress, // Arbitrator reviewing
        Resolved,   // Dispute resolved
        Cancelled   // Dispute cancelled
    }

    struct Arbitrator {
        uint256 arbitratorId;
        address arbitrator;
        string name;
        string description;
        uint256 fee; // Fee in basis points
        uint256 totalDisputes;
        uint256 resolvedDisputes;
        uint256 averageRating;
        bool isActive;
        uint256 createdAt;
    }

    struct Dispute {
        uint256 disputeId;
        uint256 rentalId;
        address initiator;
        address arbitrator;
        DisputeStatus status;
        string reason;
        string evidenceIpfsHash;
        string resolution;
        uint256 createdAt;
        uint256 assignedAt;
        uint256 resolvedAt;
        uint256 deadline;
        uint256 arbitratorFee;
    }

    // Mappings
    mapping(uint256 => Arbitrator) public arbitrators;
    mapping(address => uint256) public arbitratorAddressToId;
    mapping(uint256 => Dispute) public disputes;
    mapping(address => uint256[]) public userDisputes;
    mapping(uint256 => uint256[]) public arbitratorDisputes;
    mapping(uint256 => mapping(address => uint256)) public arbitratorRatings;
    mapping(uint256 => uint256) public disputeCountByArbitrator;

    // Modifiers
    modifier onlyArbitrator(uint256 arbitratorId) {
        require(
            arbitrators[arbitratorId].arbitrator == msg.sender,
            "Only the assigned arbitrator can perform this action"
        );
        _;
    }

    modifier onlyInitiator(uint256 disputeId) {
        require(
            disputes[disputeId].initiator == msg.sender,
            "Only dispute initiator can perform this action"
        );
        _;
    }

    modifier disputeExists(uint256 disputeId) {
        require(disputes[disputeId].initiator != address(0), "Dispute does not exist");
        _;
    }

    modifier validDisputeStatus(uint256 disputeId, DisputeStatus expectedStatus) {
        require(disputes[disputeId].status == expectedStatus, "Invalid dispute status");
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Register a new arbitrator
     * @param name Arbitrator name
     * @param description Arbitrator description
     * @param fee Fee in basis points (0-1000 = 0-10%)
     */
    function registerArbitrator(
        string memory name,
        string memory description,
        uint256 fee
    ) external {
        require(bytes(name).length > 0, "Name cannot be empty");
        require(fee <= 1000, "Fee cannot exceed 10%");
        require(arbitratorAddressToId[msg.sender] == 0, "Arbitrator already registered");

        uint256 arbitratorId = _arbitratorIdCounter.current();
        _arbitratorIdCounter.increment();

        Arbitrator memory newArbitrator = Arbitrator({
            arbitratorId: arbitratorId,
            arbitrator: msg.sender,
            name: name,
            description: description,
            fee: fee,
            totalDisputes: 0,
            resolvedDisputes: 0,
            averageRating: 0,
            isActive: true,
            createdAt: block.timestamp
        });

        arbitrators[arbitratorId] = newArbitrator;
        arbitratorAddressToId[msg.sender] = arbitratorId;
        _grantRole(ARBITRATOR_ROLE, msg.sender);

        emit ArbitratorRegistered(arbitratorId, msg.sender, name, description, fee);
    }

    /**
     * @dev Remove an arbitrator (admin only)
     * @param arbitratorId ID of the arbitrator to remove
     */
    function removeArbitrator(uint256 arbitratorId) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        require(arbitrators[arbitratorId].arbitrator != address(0), "Arbitrator does not exist");
        
        arbitrators[arbitratorId].isActive = false;
        _revokeRole(ARBITRATOR_ROLE, arbitrators[arbitratorId].arbitrator);
        
        emit ArbitratorRemoved(arbitratorId, arbitrators[arbitratorId].arbitrator);
    }

    /**
     * @dev Create a new dispute
     * @param rentalId ID of the rental
     * @param reason Reason for dispute
     * @param evidenceIpfsHash IPFS hash with evidence
     */
    function createDispute(
        uint256 rentalId,
        string memory reason,
        string memory evidenceIpfsHash
    ) external whenNotPaused {
        require(bytes(reason).length > 0, "Reason cannot be empty");
        require(bytes(evidenceIpfsHash).length > 0, "Evidence hash cannot be empty");

        uint256 disputeId = _disputeIdCounter.current();
        _disputeIdCounter.increment();

        Dispute memory newDispute = Dispute({
            disputeId: disputeId,
            rentalId: rentalId,
            initiator: msg.sender,
            arbitrator: address(0),
            status: DisputeStatus.Pending,
            reason: reason,
            evidenceIpfsHash: evidenceIpfsHash,
            resolution: "",
            createdAt: block.timestamp,
            assignedAt: 0,
            resolvedAt: 0,
            deadline: 0,
            arbitratorFee: 0
        });

        disputes[disputeId] = newDispute;
        userDisputes[msg.sender].push(disputeId);
    }

    /**
     * @dev Assign arbitrator to dispute (admin only)
     * @param disputeId ID of the dispute
     * @param arbitratorId ID of the arbitrator
     */
    function assignArbitrator(uint256 disputeId, uint256 arbitratorId) 
        external 
        onlyRole(ADMIN_ROLE) 
        disputeExists(disputeId) 
        validDisputeStatus(disputeId, DisputeStatus.Pending) 
    {
        require(arbitrators[arbitratorId].isActive, "Arbitrator is not active");
        require(arbitrators[arbitratorId].arbitrator != address(0), "Arbitrator does not exist");

        disputes[disputeId].arbitrator = arbitrators[arbitratorId].arbitrator;
        disputes[disputeId].status = DisputeStatus.Assigned;
        disputes[disputeId].assignedAt = block.timestamp;
        disputes[disputeId].deadline = block.timestamp + 7 days; // 7 days to resolve
        disputes[disputeId].arbitratorFee = arbitrators[arbitratorId].fee;

        arbitrators[arbitratorId].totalDisputes++;
        arbitratorDisputes[arbitratorId].push(disputeId);

        emit DisputeAssigned(disputeId, disputes[disputeId].rentalId, arbitrators[arbitratorId].arbitrator, disputes[disputeId].deadline);
    }

    /**
     * @dev Start dispute review (arbitrator only)
     * @param disputeId ID of the dispute
     */
    function startDisputeReview(uint256 disputeId) 
        external 
        onlyArbitrator(arbitratorAddressToId[msg.sender]) 
        disputeExists(disputeId) 
        validDisputeStatus(disputeId, DisputeStatus.Assigned) 
    {
        require(disputes[disputeId].arbitrator == msg.sender, "Not assigned to this dispute");
        
        disputes[disputeId].status = DisputeStatus.InProgress;
    }

    /**
     * @dev Resolve dispute (arbitrator only)
     * @param disputeId ID of the dispute
     * @param resolved Whether dispute is resolved in favor of initiator
     * @param resolution Resolution details
     */
    function resolveDispute(
        uint256 disputeId,
        bool resolved,
        string memory resolution
    ) external 
        onlyArbitrator(arbitratorAddressToId[msg.sender]) 
        disputeExists(disputeId) 
        validDisputeStatus(disputeId, DisputeStatus.InProgress) 
    {
        require(disputes[disputeId].arbitrator == msg.sender, "Not assigned to this dispute");
        require(block.timestamp <= disputes[disputeId].deadline, "Dispute deadline exceeded");

        disputes[disputeId].status = DisputeStatus.Resolved;
        disputes[disputeId].resolvedAt = block.timestamp;
        disputes[disputeId].resolution = resolution;

        arbitrators[arbitratorAddressToId[msg.sender]].resolvedDisputes++;

        emit DisputeResolved(disputeId, msg.sender, resolved, resolution);
    }

    /**
     * @dev Rate an arbitrator
     * @param arbitratorId ID of the arbitrator
     * @param rating Rating (1-5)
     * @param feedback Feedback text
     */
    function rateArbitrator(
        uint256 arbitratorId,
        uint256 rating,
        string memory feedback
    ) external {
        require(arbitrators[arbitratorId].arbitrator != address(0), "Arbitrator does not exist");
        require(rating >= 1 && rating <= 5, "Rating must be between 1 and 5");
        require(arbitratorRatings[arbitratorId][msg.sender] == 0, "Already rated this arbitrator");

        arbitratorRatings[arbitratorId][msg.sender] = rating;
        
        // Update average rating
        uint256 totalRatings = disputeCountByArbitrator[arbitratorId] + 1;
        arbitrators[arbitratorId].averageRating = 
            (arbitrators[arbitratorId].averageRating * disputeCountByArbitrator[arbitratorId] + rating) / totalRatings;
        
        disputeCountByArbitrator[arbitratorId] = totalRatings;

        emit ArbitratorRated(arbitratorId, msg.sender, rating, feedback);
    }

    /**
     * @dev Get arbitrator details
     * @param arbitratorId ID of the arbitrator
     * @return Arbitrator struct
     */
    function getArbitrator(uint256 arbitratorId) external view returns (Arbitrator memory) {
        require(arbitrators[arbitratorId].arbitrator != address(0), "Arbitrator does not exist");
        return arbitrators[arbitratorId];
    }

    /**
     * @dev Get dispute details
     * @param disputeId ID of the dispute
     * @return Dispute struct
     */
    function getDispute(uint256 disputeId) external view returns (Dispute memory) {
        require(disputes[disputeId].initiator != address(0), "Dispute does not exist");
        return disputes[disputeId];
    }

    /**
     * @dev Get active arbitrators
     * @return Array of arbitrator IDs
     */
    function getActiveArbitrators() external view returns (uint256[] memory) {
        uint256 total = _arbitratorIdCounter.current();
        uint256 activeCount = 0;
        
        // Count active arbitrators
        for (uint256 i = 0; i < total; i++) {
            if (arbitrators[i].isActive) {
                activeCount++;
            }
        }
        
        // Create array with active arbitrators
        uint256[] memory activeArbitrators = new uint256[](activeCount);
        uint256 index = 0;
        
        for (uint256 i = 0; i < total; i++) {
            if (arbitrators[i].isActive) {
                activeArbitrators[index] = i;
                index++;
            }
        }
        
        return activeArbitrators;
    }

    /**
     * @dev Get user disputes
     * @param user Address of the user
     * @return Array of dispute IDs
     */
    function getUserDisputes(address user) external view returns (uint256[] memory) {
        return userDisputes[user];
    }

    /**
     * @dev Get arbitrator disputes
     * @param arbitratorId ID of the arbitrator
     * @return Array of dispute IDs
     */
    function getArbitratorDisputes(uint256 arbitratorId) external view returns (uint256[] memory) {
        return arbitratorDisputes[arbitratorId];
    }

    /**
     * @dev Calculate arbitrator fee
     * @param disputeAmount Amount in dispute
     * @param arbitratorId ID of the arbitrator
     * @return Fee amount
     */
    function calculateArbitratorFee(uint256 disputeAmount, uint256 arbitratorId) 
        external 
        view 
        returns (uint256) 
    {
        require(arbitrators[arbitratorId].arbitrator != address(0), "Arbitrator does not exist");
        return (disputeAmount * arbitrators[arbitratorId].fee) / BASIS_POINTS;
    }

    /**
     * @dev Pause contract (emergency only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

contracts/libraries/Counters.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

library Counters {
    struct Counter {
        uint256 _value;
    }

    function current(Counter storage counter) internal view returns (uint256) {
        return counter._value;
    }

    function increment(Counter storage counter) internal {
        unchecked {
            counter._value += 1;
        }
    }

    function decrement(Counter storage counter) internal {
        uint256 value = counter._value;
        require(value > 0, "Counter: decrement overflow");
        unchecked {
            counter._value = value - 1;
        }
    }

    function reset(Counter storage counter) internal {
        counter._value = 0;
    }
}

contracts/ListingRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./libraries/Counters.sol";

/**
 * @title ListingRegistry
 * @dev Manages property listings on the platform
 * @notice Stores property metadata, handles listing creation, updates, and queries
 */
contract ListingRegistry is AccessControl, ReentrancyGuard {
    using Counters for Counters.Counter;

    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    // State variables
    Counters.Counter private _listingIdCounter;
    
    // Platform fee (2% = 200 basis points)
    uint256 public constant PLATFORM_FEE_BPS = 200;
    uint256 public constant BASIS_POINTS = 10000;

    // Events
    event ListingCreated(
        uint256 indexed listingId,
        address indexed landlord,
        string title,
        uint256 pricePerDay,
        uint256 deposit,
        string ipfsHash
    );
    
    event ListingUpdated(
        uint256 indexed listingId,
        string title,
        uint256 pricePerDay,
        uint256 deposit,
        string ipfsHash
    );
    
    event ListingStatusChanged(
        uint256 indexed listingId,
        bool isActive
    );

    // Structs
    struct PropertyListing {
        uint256 listingId;
        address landlord;
        string title;
        string description;
        uint256 pricePerDay; // in wei
        uint256 deposit; // in wei
        bool isActive;
        string ipfsHash; // IPFS hash for property images and metadata
        uint256 createdAt;
        uint256 updatedAt;
    }

    // Mappings
    mapping(uint256 => PropertyListing) public listings;
    mapping(address => uint256[]) public landlordListings;
    mapping(string => bool) public usedIpfsHashes; // Prevent duplicate IPFS hashes

    // Modifiers
    modifier onlyLandlord(uint256 listingId) {
        require(
            listings[listingId].landlord == msg.sender,
            "Only landlord can perform this action"
        );
        _;
    }

    modifier listingExists(uint256 listingId) {
        require(listings[listingId].landlord != address(0), "Listing does not exist");
        _;
    }

    modifier validIpfsHash(string memory ipfsHash) {
        require(bytes(ipfsHash).length > 0, "IPFS hash cannot be empty");
        require(!usedIpfsHashes[ipfsHash], "IPFS hash already used");
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new property listing
     * @param landlord Address of the landlord
     * @param title Property title
     * @param description Property description
     * @param pricePerDay Daily rental price in wei
     * @param deposit Security deposit in wei
     * @param ipfsHash IPFS hash containing property images and metadata
     */
    function createListing(
        address landlord,
        string memory title,
        string memory description,
        uint256 pricePerDay,
        uint256 deposit,
        string memory ipfsHash
    ) external nonReentrant validIpfsHash(ipfsHash) {
        require(landlord != address(0), "Invalid landlord address");
        require(pricePerDay > 0, "Price must be greater than 0");
        require(deposit > 0, "Deposit must be greater than 0");
        require(bytes(title).length > 0, "Title cannot be empty");

        uint256 listingId = _listingIdCounter.current();
        _listingIdCounter.increment();

        PropertyListing memory newListing = PropertyListing({
            listingId: listingId,
            landlord: landlord,
            title: title,
            description: description,
            pricePerDay: pricePerDay,
            deposit: deposit,
            isActive: true,
            ipfsHash: ipfsHash,
            createdAt: block.timestamp,
            updatedAt: block.timestamp
        });

        listings[listingId] = newListing;
        landlordListings[landlord].push(listingId);
        usedIpfsHashes[ipfsHash] = true;

        emit ListingCreated(listingId, landlord, title, pricePerDay, deposit, ipfsHash);
    }

    /**
     * @dev Update an existing listing (only landlord)
     * @param listingId ID of the listing to update
     * @param title New title
     * @param description New description
     * @param pricePerDay New daily price
     * @param deposit New deposit amount
     * @param ipfsHash New IPFS hash
     */
    function updateListing(
        uint256 listingId,
        string memory title,
        string memory description,
        uint256 pricePerDay,
        uint256 deposit,
        string memory ipfsHash
    ) external 
        nonReentrant 
        onlyLandlord(listingId) 
        listingExists(listingId) 
    {
        require(pricePerDay > 0, "Price must be greater than 0");
        require(deposit > 0, "Deposit must be greater than 0");
        require(bytes(title).length > 0, "Title cannot be empty");

        // If IPFS hash is different, check if it's not already used
        if (keccak256(bytes(listings[listingId].ipfsHash)) != keccak256(bytes(ipfsHash))) {
            require(!usedIpfsHashes[ipfsHash], "IPFS hash already used");
            usedIpfsHashes[listings[listingId].ipfsHash] = false; // Free old hash
            usedIpfsHashes[ipfsHash] = true; // Mark new hash as used
        }

        listings[listingId].title = title;
        listings[listingId].description = description;
        listings[listingId].pricePerDay = pricePerDay;
        listings[listingId].deposit = deposit;
        listings[listingId].ipfsHash = ipfsHash;
        listings[listingId].updatedAt = block.timestamp;

        emit ListingUpdated(listingId, title, pricePerDay, deposit, ipfsHash);
    }

    /**
     * @dev Toggle listing active status
     * @param listingId ID of the listing
     */
    function toggleListingStatus(uint256 listingId) 
        external 
        onlyLandlord(listingId) 
        listingExists(listingId) 
    {
        listings[listingId].isActive = !listings[listingId].isActive;
        emit ListingStatusChanged(listingId, listings[listingId].isActive);
    }

    /**
     * @dev Get listing details
     * @param listingId ID of the listing
     * @return PropertyListing struct
     */
    function getListing(uint256 listingId) 
        external 
        view 
        listingExists(listingId) 
        returns (PropertyListing memory) 
    {
        return listings[listingId];
    }

    /**
     * @dev Get all listings for a landlord
     * @param landlord Address of the landlord
     * @return Array of listing IDs
     */
    function getLandlordListings(address landlord) external view returns (uint256[] memory) {
        return landlordListings[landlord];
    }

    /**
     * @dev Get total number of listings
     * @return Total count
     */
    function getTotalListings() external view returns (uint256) {
        return _listingIdCounter.current();
    }

    /**
     * @dev Get active listings count
     * @return Active listings count
     */
    function getActiveListingsCount() external view returns (uint256) {
        uint256 total = _listingIdCounter.current();
        uint256 activeCount = 0;
        
        for (uint256 i = 0; i < total; i++) {
            if (listings[i].isActive) {
                activeCount++;
            }
        }
        
        return activeCount;
    }

    /**
     * @dev Calculate platform fee for a transaction
     * @param amount Transaction amount
     * @return Fee amount
     */
    function calculatePlatformFee(uint256 amount) external pure returns (uint256) {
        return (amount * PLATFORM_FEE_BPS) / BASIS_POINTS;
    }

    /**
     * @dev Emergency function to deactivate a listing (admin only)
     * @param listingId ID of the listing
     */
    function emergencyDeactivateListing(uint256 listingId) 
        external 
        onlyRole(ADMIN_ROLE) 
        listingExists(listingId) 
    {
        listings[listingId].isActive = false;
        emit ListingStatusChanged(listingId, false);
    }
}

contracts/RentalEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./libraries/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title RentalEscrow
 * @dev Manages rental deposits and payments with escrow functionality
 * @notice Handles deposit, payment, refund, and dispute resolution
 */
contract RentalEscrow is AccessControl, ReentrancyGuard, Pausable {
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;

    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    // State variables
    Counters.Counter private _rentalIdCounter;
    
    // Platform fee (2% = 200 basis points)
    uint256 public constant PLATFORM_FEE_BPS = 200;
    uint256 public constant BASIS_POINTS = 10000;
    
    // Dispute timeout (7 days)
    uint256 public constant DISPUTE_TIMEOUT = 7 days;

    // Events
    event RentalCreated(
        uint256 indexed rentalId,
        uint256 indexed listingId,
        address indexed tenant,
        address landlord,
        uint256 deposit,
        uint256 totalRent,
        uint256 startDate,
        uint256 endDate
    );
    
    event DepositMade(
        uint256 indexed rentalId,
        address indexed tenant,
        uint256 amount
    );
    
    event PaymentMade(
        uint256 indexed rentalId,
        address indexed tenant,
        uint256 amount
    );
    
    event RefundProcessed(
        uint256 indexed rentalId,
        address indexed recipient,
        uint256 amount
    );
    
    event DisputeOpened(
        uint256 indexed rentalId,
        address indexed initiator,
        string reason,
        string ipfsHash
    );
    
    event DisputeResolved(
        uint256 indexed rentalId,
        address indexed arbitrator,
        uint256 tenantRefund,
        uint256 landlordRefund,
        uint256 platformFee
    );

    // Structs
    enum RentalStatus {
        Pending,    // Deposit not yet made
        Active,     // Deposit made, rental active
        Completed,  // Rental completed, awaiting refund
        Disputed,   // Dispute opened
        Resolved    // Dispute resolved
    }

    struct Rental {
        uint256 rentalId;
        uint256 listingId;
        address tenant;
        address landlord;
        uint256 deposit;
        uint256 totalRent;
        uint256 startDate;
        uint256 endDate;
        RentalStatus status;
        uint256 createdAt;
        uint256 updatedAt;
        string contractIpfsHash; // PDF contract hash
        bool tenantSigned;
        bool landlordSigned;
        address arbitrator;
        uint256 disputeOpenedAt;
        string disputeReason;
        string disputeIpfsHash;
    }

    // Mappings
    mapping(uint256 => Rental) public rentals;
    mapping(address => uint256[]) public tenantRentals;
    mapping(address => uint256[]) public landlordRentals;
    mapping(address => uint256) public userDisputeCount;
    mapping(address => uint256) public lastDisputeTime;

    // Constants
    uint256 public constant MAX_DISPUTES_PER_USER = 3;
    uint256 public constant DISPUTE_COOLDOWN = 30 days;

    // Modifiers
    modifier onlyParticipant(uint256 rentalId) {
        require(
            rentals[rentalId].tenant == msg.sender || 
            rentals[rentalId].landlord == msg.sender,
            "Only rental participants can perform this action"
        );
        _;
    }

    modifier onlyArbitrator(uint256 rentalId) {
        require(
            hasRole(ARBITRATOR_ROLE, msg.sender) || 
            rentals[rentalId].arbitrator == msg.sender,
            "Only arbitrator can perform this action"
        );
        _;
    }

    modifier rentalExists(uint256 rentalId) {
        require(rentals[rentalId].tenant != address(0), "Rental does not exist");
        _;
    }

    modifier validRentalStatus(uint256 rentalId, RentalStatus expectedStatus) {
        require(rentals[rentalId].status == expectedStatus, "Invalid rental status");
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new rental agreement
     * @param listingId ID of the property listing
     * @param landlord Address of the landlord
     * @param deposit Security deposit amount
     * @param totalRent Total rent amount
     * @param startDate Rental start date (timestamp)
     * @param endDate Rental end date (timestamp)
     */
    function createRental(
        uint256 listingId,
        address landlord,
        uint256 deposit,
        uint256 totalRent,
        uint256 startDate,
        uint256 endDate
    ) external whenNotPaused {
        require(landlord != address(0), "Invalid landlord address");
        require(landlord != msg.sender, "Cannot rent from yourself");
        require(deposit > 0, "Deposit must be greater than 0");
        require(totalRent > 0, "Total rent must be greater than 0");
        require(startDate > block.timestamp, "Start date must be in the future");
        require(endDate > startDate, "End date must be after start date");

        uint256 rentalId = _rentalIdCounter.current();
        _rentalIdCounter.increment();

        Rental memory newRental = Rental({
            rentalId: rentalId,
            listingId: listingId,
            tenant: msg.sender,
            landlord: landlord,
            deposit: deposit,
            totalRent: totalRent,
            startDate: startDate,
            endDate: endDate,
            status: RentalStatus.Pending,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            contractIpfsHash: "",
            tenantSigned: false,
            landlordSigned: false,
            arbitrator: address(0),
            disputeOpenedAt: 0,
            disputeReason: "",
            disputeIpfsHash: ""
        });

        rentals[rentalId] = newRental;
        tenantRentals[msg.sender].push(rentalId);
        landlordRentals[landlord].push(rentalId);

        emit RentalCreated(rentalId, listingId, msg.sender, landlord, deposit, totalRent, startDate, endDate);
    }

    /**
     * @dev Make deposit for rental (MATIC)
     * @param rentalId ID of the rental
     */
    function makeDeposit(uint256 rentalId) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
        rentalExists(rentalId) 
        validRentalStatus(rentalId, RentalStatus.Pending) 
    {
        require(rentals[rentalId].tenant == msg.sender, "Only tenant can make deposit");
        require(msg.value >= rentals[rentalId].deposit, "Insufficient deposit amount");

        rentals[rentalId].status = RentalStatus.Active;
        rentals[rentalId].updatedAt = block.timestamp;

        emit DepositMade(rentalId, msg.sender, msg.value);
    }

    /**
     * @dev Make deposit using ERC20 token
     * @param rentalId ID of the rental
     * @param token ERC20 token address
     */
    function makeDepositWithToken(uint256 rentalId, address token) 
        external 
        nonReentrant 
        whenNotPaused 
        rentalExists(rentalId) 
        validRentalStatus(rentalId, RentalStatus.Pending) 
    {
        require(rentals[rentalId].tenant == msg.sender, "Only tenant can make deposit");
        
        IERC20 tokenContract = IERC20(token);
        uint256 depositAmount = rentals[rentalId].deposit;
        
        tokenContract.safeTransferFrom(msg.sender, address(this), depositAmount);
        
        rentals[rentalId].status = RentalStatus.Active;
        rentals[rentalId].updatedAt = block.timestamp;

        emit DepositMade(rentalId, msg.sender, depositAmount);
    }

    /**
     * @dev Sign rental contract
     * @param rentalId ID of the rental
     * @param contractIpfsHash IPFS hash of the signed PDF contract
     */
    function signContract(uint256 rentalId, string memory contractIpfsHash) 
        external 
        onlyParticipant(rentalId) 
        rentalExists(rentalId) 
    {
        require(bytes(contractIpfsHash).length > 0, "Contract hash cannot be empty");
        
        if (rentals[rentalId].tenant == msg.sender) {
            rentals[rentalId].tenantSigned = true;
        } else if (rentals[rentalId].landlord == msg.sender) {
            rentals[rentalId].landlordSigned = true;
        }
        
        if (bytes(rentals[rentalId].contractIpfsHash).length == 0) {
            rentals[rentalId].contractIpfsHash = contractIpfsHash;
        }
        
        rentals[rentalId].updatedAt = block.timestamp;
    }

    /**
     * @dev Agree to return deposit (both parties must agree)
     * @param rentalId ID of the rental
     */
    function agreeReturn(uint256 rentalId) 
        external 
        onlyParticipant(rentalId) 
        rentalExists(rentalId) 
        validRentalStatus(rentalId, RentalStatus.Active) 
    {
        require(
            rentals[rentalId].tenantSigned && rentals[rentalId].landlordSigned,
            "Both parties must sign the contract first"
        );
        require(block.timestamp >= rentals[rentalId].endDate, "Rental period not ended");

        rentals[rentalId].status = RentalStatus.Completed;
        rentals[rentalId].updatedAt = block.timestamp;

        // Process refund
        _processRefund(rentalId);
    }

    /**
     * @dev Open a dispute
     * @param rentalId ID of the rental
     * @param reason Reason for dispute
     * @param disputeIpfsHash IPFS hash with dispute evidence
     */
    function openDispute(
        uint256 rentalId,
        string memory reason,
        string memory disputeIpfsHash
    ) external 
        onlyParticipant(rentalId) 
        rentalExists(rentalId) 
        validRentalStatus(rentalId, RentalStatus.Active) 
    {
        require(bytes(reason).length > 0, "Dispute reason cannot be empty");
        require(
            userDisputeCount[msg.sender] < MAX_DISPUTES_PER_USER,
            "Maximum disputes per user exceeded"
        );
        require(
            block.timestamp >= lastDisputeTime[msg.sender] + DISPUTE_COOLDOWN,
            "Dispute cooldown not met"
        );

        rentals[rentalId].status = RentalStatus.Disputed;
        rentals[rentalId].disputeOpenedAt = block.timestamp;
        rentals[rentalId].disputeReason = reason;
        rentals[rentalId].disputeIpfsHash = disputeIpfsHash;
        rentals[rentalId].updatedAt = block.timestamp;

        userDisputeCount[msg.sender]++;
        lastDisputeTime[msg.sender] = block.timestamp;

        emit DisputeOpened(rentalId, msg.sender, reason, disputeIpfsHash);
    }

    /**
     * @dev Resolve a dispute (arbitrator only)
     * @param rentalId ID of the rental
     * @param tenantRefund Amount to refund to tenant
     * @param landlordRefund Amount to refund to landlord
     * @param platformFee Platform fee amount
     */
    function resolveDispute(
        uint256 rentalId,
        uint256 tenantRefund,
        uint256 landlordRefund,
        uint256 platformFee
    ) external 
        onlyArbitrator(rentalId) 
        rentalExists(rentalId) 
        validRentalStatus(rentalId, RentalStatus.Disputed) 
    {
        require(
            tenantRefund + landlordRefund + platformFee <= rentals[rentalId].deposit,
            "Total refunds exceed deposit amount"
        );

        rentals[rentalId].status = RentalStatus.Resolved;
        rentals[rentalId].arbitrator = msg.sender;
        rentals[rentalId].updatedAt = block.timestamp;

        // Process refunds
        if (tenantRefund > 0) {
            payable(rentals[rentalId].tenant).transfer(tenantRefund);
        }
        if (landlordRefund > 0) {
            payable(rentals[rentalId].landlord).transfer(landlordRefund);
        }
        if (platformFee > 0) {
            // Transfer to contract owner (first admin)
            payable(address(this)).transfer(platformFee);
        }

        emit DisputeResolved(rentalId, msg.sender, tenantRefund, landlordRefund, platformFee);
    }

    /**
     * @dev Internal function to process refund
     * @param rentalId ID of the rental
     */
    function _processRefund(uint256 rentalId) internal {
        uint256 deposit = rentals[rentalId].deposit;
        uint256 platformFee = (deposit * PLATFORM_FEE_BPS) / BASIS_POINTS;
        uint256 refundAmount = deposit - platformFee;

        // Refund to tenant
        payable(rentals[rentalId].tenant).transfer(refundAmount);
        
        // Platform fee to contract
        if (platformFee > 0) {
            payable(address(this)).transfer(platformFee);
        }

        emit RefundProcessed(rentalId, rentals[rentalId].tenant, refundAmount);
    }

    /**
     * @dev Get rental details
     * @param rentalId ID of the rental
     * @return Rental struct
     */
    function getRental(uint256 rentalId) 
        external 
        view 
        rentalExists(rentalId) 
        returns (Rental memory) 
    {
        return rentals[rentalId];
    }

    /**
     * @dev Get tenant rentals
     * @param tenant Address of the tenant
     * @return Array of rental IDs
     */
    function getTenantRentals(address tenant) external view returns (uint256[] memory) {
        return tenantRentals[tenant];
    }

    /**
     * @dev Get landlord rentals
     * @param landlord Address of the landlord
     * @return Array of rental IDs
     */
    function getLandlordRentals(address landlord) external view returns (uint256[] memory) {
        return landlordRentals[landlord];
    }

    /**
     * @dev Pause contract (emergency only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Get contract balance
     * @return Contract ETH balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // Fallback function to receive ETH
    receive() external payable {}
}

contracts/Reputation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./libraries/Counters.sol";

/**
 * @title Reputation
 * @dev Manages user reputation and rating system
 * @notice Handles rating aggregation, review management, and reputation scoring
 */
contract Reputation is AccessControl, ReentrancyGuard, Pausable {
    using Counters for Counters.Counter;

    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    // State variables
    Counters.Counter private _reviewIdCounter;

    // Rating constants
    uint256 public constant MIN_RATING = 1;
    uint256 public constant MAX_RATING = 5;
    uint256 public constant RATING_DECIMALS = 2; // 2 decimal places for precision

    // Reputation thresholds
    uint256 public constant EXCELLENT_THRESHOLD = 450; // 4.5/5.0
    uint256 public constant GOOD_THRESHOLD = 350;      // 3.5/5.0
    uint256 public constant FAIR_THRESHOLD = 250;      // 2.5/5.0

    // Events
    event ReviewCreated(
        uint256 indexed reviewId,
        address indexed reviewer,
        address indexed reviewee,
        uint256 rating,
        string review,
        string ipfsHash,
        uint256 rentalId
    );
    
    event ReviewUpdated(
        uint256 indexed reviewId,
        uint256 newRating,
        string newReview
    );
    
    event ReviewDeleted(
        uint256 indexed reviewId,
        address indexed reviewer,
        address indexed reviewee
    );
    
    event ReputationUpdated(
        address indexed user,
        uint256 newReputationScore,
        uint256 totalReviews
    );

    // Structs
    enum ReviewType {
        TenantToLandlord,  // 0 - Tenant reviewing landlord
        LandlordToTenant   // 1 - Landlord reviewing tenant
    }

    struct Review {
        uint256 reviewId;
        address reviewer;
        address reviewee;
        uint256 rating;
        string review;
        string ipfsHash; // IPFS hash for additional evidence/media
        uint256 rentalId;
        ReviewType reviewType;
        uint256 createdAt;
        uint256 updatedAt;
        bool isDeleted;
    }

    struct UserReputation {
        address user;
        uint256 totalReviews;
        uint256 totalRatingSum;
        uint256 reputationScore; // Average rating * 100 (e.g., 4.5 = 450)
        uint256 completedRentals;
        uint256 cancelledRentals;
        uint256 disputeCount;
        bool isVerified;
        uint256 lastUpdated;
    }

    // Mappings
    mapping(uint256 => Review) public reviews;
    mapping(address => UserReputation) public userReputations;
    mapping(address => uint256[]) public userReviews;
    mapping(address => uint256[]) public userReceivedReviews;
    mapping(address => mapping(address => uint256)) public userToUserReviews; // reviewer => reviewee => reviewId
    mapping(uint256 => uint256) public rentalReviews; // rentalId => reviewId

    // Modifiers
    modifier onlyReviewer(uint256 reviewId) {
        require(reviews[reviewId].reviewer == msg.sender, "Only reviewer can perform this action");
        _;
    }

    modifier reviewExists(uint256 reviewId) {
        require(reviews[reviewId].reviewer != address(0), "Review does not exist");
        _;
    }

    modifier validRating(uint256 rating) {
        require(rating >= MIN_RATING && rating <= MAX_RATING, "Rating must be between 1 and 5");
        _;
    }

    modifier notSelfReview(address reviewee) {
        require(msg.sender != reviewee, "Cannot review yourself");
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new review
     * @param reviewee Address of the user being reviewed
     * @param rating Rating (1-5)
     * @param review Review text
     * @param ipfsHash IPFS hash for additional evidence
     * @param rentalId ID of the rental
     * @param reviewType Type of review (tenant to landlord or vice versa)
     */
    function createReview(
        address reviewee,
        uint256 rating,
        string memory review,
        string memory ipfsHash,
        uint256 rentalId,
        ReviewType reviewType
    ) external 
        nonReentrant 
        whenNotPaused 
        validRating(rating) 
        notSelfReview(reviewee) 
    {
        require(bytes(review).length > 0, "Review text cannot be empty");
        require(reviewee != address(0), "Invalid reviewee address");
        
        // Check if user already reviewed this person for this rental
        require(
            userToUserReviews[msg.sender][reviewee] == 0 || 
            reviews[userToUserReviews[msg.sender][reviewee]].rentalId != rentalId,
            "Already reviewed this user for this rental"
        );

        uint256 reviewId = _reviewIdCounter.current();
        _reviewIdCounter.increment();

        Review memory newReview = Review({
            reviewId: reviewId,
            reviewer: msg.sender,
            reviewee: reviewee,
            rating: rating,
            review: review,
            ipfsHash: ipfsHash,
            rentalId: rentalId,
            reviewType: reviewType,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            isDeleted: false
        });

        reviews[reviewId] = newReview;
        userReviews[msg.sender].push(reviewId);
        userReceivedReviews[reviewee].push(reviewId);
        userToUserReviews[msg.sender][reviewee] = reviewId;
        rentalReviews[rentalId] = reviewId;

        // Update reputation
        _updateReputation(reviewee);

        emit ReviewCreated(reviewId, msg.sender, reviewee, rating, review, ipfsHash, rentalId);
    }

    /**
     * @dev Update an existing review
     * @param reviewId ID of the review
     * @param newRating New rating
     * @param newReview New review text
     */
    function updateReview(
        uint256 reviewId,
        uint256 newRating,
        string memory newReview
    ) external 
        onlyReviewer(reviewId) 
        reviewExists(reviewId) 
        validRating(newRating) 
    {
        require(!reviews[reviewId].isDeleted, "Review is deleted");
        require(bytes(newReview).length > 0, "Review text cannot be empty");
        require(block.timestamp <= reviews[reviewId].createdAt + 7 days, "Cannot update review after 7 days");

        address reviewee = reviews[reviewId].reviewee;

        reviews[reviewId].rating = newRating;
        reviews[reviewId].review = newReview;
        reviews[reviewId].updatedAt = block.timestamp;

        // Update reputation with new rating
        _updateReputation(reviewee);

        emit ReviewUpdated(reviewId, newRating, newReview);
    }

    /**
     * @dev Delete a review
     * @param reviewId ID of the review
     */
    function deleteReview(uint256 reviewId) 
        external 
        onlyReviewer(reviewId) 
        reviewExists(reviewId) 
    {
        require(!reviews[reviewId].isDeleted, "Review already deleted");
        require(block.timestamp <= reviews[reviewId].createdAt + 7 days, "Cannot delete review after 7 days");

        address reviewee = reviews[reviewId].reviewee;
        
        reviews[reviewId].isDeleted = true;
        reviews[reviewId].updatedAt = block.timestamp;

        // Update reputation after deletion
        _updateReputation(reviewee);

        emit ReviewDeleted(reviewId, msg.sender, reviewee);
    }

    /**
     * @dev Update user's rental statistics
     * @param user Address of the user
     * @param completed Whether rental was completed
     * @param cancelled Whether rental was cancelled
     * @param hadDispute Whether rental had a dispute
     */
    function updateRentalStats(
        address user,
        bool completed,
        bool cancelled,
        bool hadDispute
    ) external onlyRole(ADMIN_ROLE) {
        UserReputation storage reputation = userReputations[user];
        
        if (completed) {
            reputation.completedRentals++;
        }
        if (cancelled) {
            reputation.cancelledRentals++;
        }
        if (hadDispute) {
            reputation.disputeCount++;
        }
        
        reputation.lastUpdated = block.timestamp;
    }

    /**
     * @dev Verify a user (admin only)
     * @param user Address of the user
     */
    function verifyUser(address user) external onlyRole(ADMIN_ROLE) {
        userReputations[user].isVerified = true;
        userReputations[user].lastUpdated = block.timestamp;
    }

    /**
     * @dev Get user reputation
     * @param user Address of the user
     * @return UserReputation struct
     */
    function getUserReputation(address user) external view returns (UserReputation memory) {
        return userReputations[user];
    }

    /**
     * @dev Get review details
     * @param reviewId ID of the review
     * @return Review struct
     */
    function getReview(uint256 reviewId) external view returns (Review memory) {
        require(reviews[reviewId].reviewer != address(0), "Review does not exist");
        return reviews[reviewId];
    }

    /**
     * @dev Get user's reviews
     * @param user Address of the user
     * @return Array of review IDs
     */
    function getUserReviews(address user) external view returns (uint256[] memory) {
        return userReviews[user];
    }

    /**
     * @dev Get reviews received by user
     * @param user Address of the user
     * @return Array of review IDs
     */
    function getUserReceivedReviews(address user) external view returns (uint256[] memory) {
        return userReceivedReviews[user];
    }

    /**
     * @dev Get reputation level based on score
     * @param score Reputation score
     * @return Reputation level string
     */
    function getReputationLevel(uint256 score) external pure returns (string memory) {
        if (score >= EXCELLENT_THRESHOLD) {
            return "Excellent";
        } else if (score >= GOOD_THRESHOLD) {
            return "Good";
        } else if (score >= FAIR_THRESHOLD) {
            return "Fair";
        } else {
            return "Poor";
        }
    }

    /**
     * @dev Check if user can review another user
     * @param reviewer Address of the reviewer
     * @param reviewee Address of the reviewee
     * @param rentalId ID of the rental
     * @return Whether review is allowed
     */
    function canReview(address reviewer, address reviewee, uint256 rentalId) 
        external 
        view 
        returns (bool) 
    {
        if (reviewer == reviewee) return false;
        if (userToUserReviews[reviewer][reviewee] != 0) {
            return reviews[userToUserReviews[reviewer][reviewee]].rentalId != rentalId;
        }
        return true;
    }

    /**
     * @dev Internal function to update user reputation
     * @param user Address of the user
     */
    function _updateReputation(address user) internal {
        UserReputation storage reputation = userReputations[user];
        
        uint256 totalRatingSum = 0;
        uint256 validReviews = 0;
        
        // Calculate average rating from non-deleted reviews
        for (uint256 i = 0; i < userReceivedReviews[user].length; i++) {
            uint256 reviewId = userReceivedReviews[user][i];
            Review memory review = reviews[reviewId];
            
            if (!review.isDeleted) {
                totalRatingSum += review.rating;
                validReviews++;
            }
        }
        
        if (validReviews > 0) {
            reputation.totalRatingSum = totalRatingSum;
            reputation.totalReviews = validReviews;
            reputation.reputationScore = (totalRatingSum * 100) / validReviews; // Convert to basis points
        } else {
            reputation.reputationScore = 0;
        }
        
        reputation.lastUpdated = block.timestamp;
        
        emit ReputationUpdated(user, reputation.reputationScore, validReviews);
    }

    /**
     * @dev Pause contract (emergency only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

contracts/SmartRent.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./ListingRegistry.sol";
import "./RentalEscrow.sol";
import "./Arbitration.sol";
import "./SubscriptionManager.sol";
import "./Reputation.sol";

/**
 * @title SmartRent
 * @dev Main contract that integrates all SmartRent functionality
 * @notice Central hub for property rental platform on Polygon
 */
contract SmartRent is AccessControl, ReentrancyGuard, Pausable {
    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    // Contract addresses
    ListingRegistry public listingRegistry;
    RentalEscrow public rentalEscrow;
    Arbitration public arbitration;
    SubscriptionManager public subscriptionManager;
    Reputation public reputation;

    // Platform statistics
    uint256 public totalListings;
    uint256 public totalRentals;
    uint256 public totalDisputes;
    uint256 public totalVolume; // Total volume in wei

    // Events
    event PlatformInitialized(
        address listingRegistry,
        address rentalEscrow,
        address arbitration,
        address subscriptionManager,
        address reputation
    );
    
    event StatisticsUpdated(
        uint256 totalListings,
        uint256 totalRentals,
        uint256 totalDisputes,
        uint256 totalVolume
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Initialize platform with all contract addresses
     * @param _listingRegistry Address of ListingRegistry contract
     * @param _rentalEscrow Address of RentalEscrow contract
     * @param _arbitration Address of Arbitration contract
     * @param _subscriptionManager Address of SubscriptionManager contract
     * @param _reputation Address of Reputation contract
     */
    function initializePlatform(
        address _listingRegistry,
        address payable _rentalEscrow,
        address _arbitration,
        address payable _subscriptionManager,
        address _reputation
    ) external onlyRole(ADMIN_ROLE) {
        require(_listingRegistry != address(0), "Invalid listing registry address");
        require(_rentalEscrow != address(0), "Invalid rental escrow address");
        require(_arbitration != address(0), "Invalid arbitration address");
        require(_subscriptionManager != address(0), "Invalid subscription manager address");
        require(_reputation != address(0), "Invalid reputation address");

        listingRegistry = ListingRegistry(_listingRegistry);
        rentalEscrow = RentalEscrow(_rentalEscrow);
        arbitration = Arbitration(_arbitration);
        subscriptionManager = SubscriptionManager(_subscriptionManager);
        reputation = Reputation(_reputation);

        emit PlatformInitialized(
            _listingRegistry,
            _rentalEscrow,
            _arbitration,
            _subscriptionManager,
            _reputation
        );
    }

    /**
     * @dev Create a property listing
     * @param title Property title
     * @param description Property description
     * @param pricePerDay Daily rental price in wei
     * @param deposit Security deposit in wei
     * @param ipfsHash IPFS hash containing property images and metadata
     */
    function createListing(
        string memory title,
        string memory description,
        uint256 pricePerDay,
        uint256 deposit,
        string memory ipfsHash
    ) external whenNotPaused {
        listingRegistry.createListing(msg.sender, title, description, pricePerDay, deposit, ipfsHash);
        totalListings++;
        _updateStatistics();
        
        emit ListingCreated(totalListings - 1, msg.sender, title, pricePerDay, deposit, ipfsHash);
    }
    
    // Event for listing creation
    event ListingCreated(
        uint256 indexed listingId,
        address indexed landlord,
        string title,
        uint256 pricePerDay,
        uint256 deposit,
        string ipfsHash
    );

    /**
     * @dev Create a rental agreement
     * @param listingId ID of the property listing
     * @param landlord Address of the landlord
     * @param deposit Security deposit amount
     * @param totalRent Total rent amount
     * @param startDate Rental start date (timestamp)
     * @param endDate Rental end date (timestamp)
     */
    function createRental(
        uint256 listingId,
        address landlord,
        uint256 deposit,
        uint256 totalRent,
        uint256 startDate,
        uint256 endDate
    ) external whenNotPaused {
        rentalEscrow.createRental(listingId, landlord, deposit, totalRent, startDate, endDate);
        totalRentals++;
        totalVolume += deposit + totalRent;
        _updateStatistics();
    }

    /**
     * @dev Make deposit for rental (MATIC)
     * @param rentalId ID of the rental
     */
    function makeDeposit(uint256 rentalId) external payable whenNotPaused {
        rentalEscrow.makeDeposit{value: msg.value}(rentalId);
    }

    /**
     * @dev Make deposit using ERC20 token
     * @param rentalId ID of the rental
     * @param token ERC20 token address
     */
    function makeDepositWithToken(uint256 rentalId, address token) external whenNotPaused {
        rentalEscrow.makeDepositWithToken(rentalId, token);
    }

    /**
     * @dev Sign rental contract
     * @param rentalId ID of the rental
     * @param contractIpfsHash IPFS hash of the signed PDF contract
     */
    function signContract(uint256 rentalId, string memory contractIpfsHash) external {
        rentalEscrow.signContract(rentalId, contractIpfsHash);
    }

    /**
     * @dev Agree to return deposit
     * @param rentalId ID of the rental
     */
    function agreeReturn(uint256 rentalId) external {
        rentalEscrow.agreeReturn(rentalId);
    }

    /**
     * @dev Open a dispute
     * @param rentalId ID of the rental
     * @param reason Reason for dispute
     * @param disputeIpfsHash IPFS hash with dispute evidence
     */
    function openDispute(
        uint256 rentalId,
        string memory reason,
        string memory disputeIpfsHash
    ) external {
        rentalEscrow.openDispute(rentalId, reason, disputeIpfsHash);
        arbitration.createDispute(rentalId, reason, disputeIpfsHash);
        totalDisputes++;
        _updateStatistics();
    }

    /**
     * @dev Create a Pro subscription
     * @param plan Subscription plan
     * @param duration Duration in months
     * @param autoRenew Whether to auto-renew
     */
    function createSubscription(
        SubscriptionManager.SubscriptionPlan plan,
        uint256 duration,
        bool autoRenew
    ) external payable whenNotPaused {
        subscriptionManager.createSubscription{value: msg.value}(plan, duration, autoRenew);
    }

    /**
     * @dev Create a review
     * @param reviewee Address of the user being reviewed
     * @param rating Rating (1-5)
     * @param reviewText Review text
     * @param ipfsHash IPFS hash for additional evidence
     * @param rentalId ID of the rental
     * @param reviewType Type of review
     */
    function createReview(
        address reviewee,
        uint256 rating,
        string memory reviewText,
        string memory ipfsHash,
        uint256 rentalId,
        Reputation.ReviewType reviewType
    ) external whenNotPaused {
        reputation.createReview(reviewee, rating, reviewText, ipfsHash, rentalId, reviewType);
    }

    /**
     * @dev Get platform statistics
     * @return totalListings Total number of listings
     * @return totalRentals Total number of rentals
     * @return totalDisputes Total number of disputes
     * @return totalVolume Total volume in wei
     */
    function getPlatformStatistics() external view returns (
        uint256,
        uint256,
        uint256,
        uint256
    ) {
        return (totalListings, totalRentals, totalDisputes, totalVolume);
    }

    /**
     * @dev Get user's rental history
     * @param user Address of the user
     * @return Array of rental IDs
     */
    function getUserRentalHistory(address user) external view returns (uint256[] memory) {
        return rentalEscrow.getTenantRentals(user);
    }

    /**
     * @dev Get user's listing history
     * @param user Address of the user
     * @return Array of listing IDs
     */
    function getUserListingHistory(address user) external view returns (uint256[] memory) {
        return listingRegistry.getLandlordListings(user);
    }

    /**
     * @dev Check if user has premium features
     * @param user Address of the user
     * @param feature Feature bit
     * @return Whether user has the feature
     */
    function hasPremiumFeature(address user, uint256 feature) external view returns (bool) {
        return subscriptionManager.hasFeature(user, feature);
    }

    /**
     * @dev Get user reputation
     * @param user Address of the user
     * @return UserReputation struct
     */
    function getUserReputation(address user) external view returns (Reputation.UserReputation memory) {
        return reputation.getUserReputation(user);
    }

    /**
     * @dev Get listing details
     * @param listingId ID of the listing
     * @return PropertyListing struct
     */
    function getListing(uint256 listingId) external view returns (ListingRegistry.PropertyListing memory) {
        return listingRegistry.getListing(listingId);
    }

    /**
     * @dev Get total number of listings
     * @return Total count of listings
     */
    function getTotalListings() external view returns (uint256) {
        return listingRegistry.getTotalListings();
    }

    /**
     * @dev Get rental details
     * @param rentalId ID of the rental
     * @return Rental struct
     */
    function getRental(uint256 rentalId) external view returns (RentalEscrow.Rental memory) {
        return rentalEscrow.getRental(rentalId);
    }

    /**
     * @dev Update platform statistics
     */
    function _updateStatistics() internal {
        emit StatisticsUpdated(totalListings, totalRentals, totalDisputes, totalVolume);
    }

    /**
     * @dev Pause platform (emergency only)
     */
    function pausePlatform() external onlyRole(ADMIN_ROLE) {
        _pause();
        // Pause individual contracts if they have pause functionality
        // listingRegistry.pause(); // ListingRegistry doesn't have pause
        rentalEscrow.pause();
        arbitration.pause();
        subscriptionManager.pause();
        reputation.pause();
    }

    /**
     * @dev Unpause platform
     */
    function unpausePlatform() external onlyRole(ADMIN_ROLE) {
        _unpause();
        // Unpause individual contracts if they have pause functionality
        // listingRegistry.unpause(); // ListingRegistry doesn't have pause
        rentalEscrow.unpause();
        arbitration.unpause();
        subscriptionManager.unpause();
        reputation.unpause();
    }

    /**
     * @dev Get contract balance
     * @return Contract ETH balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Withdraw platform fees (admin only)
     * @param amount Amount to withdraw
     */
    function withdrawFees(uint256 amount) external onlyRole(ADMIN_ROLE) {
        require(amount <= address(this).balance, "Insufficient balance");
        payable(msg.sender).transfer(amount);
    }

    // Fallback function to receive ETH
    receive() external payable {}
}

contracts/SubscriptionManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./libraries/Counters.sol";

/**
 * @title SubscriptionManager
 * @dev Manages Pro subscriptions for landlords
 * @notice Handles subscription creation, renewal, and premium features
 */
contract SubscriptionManager is AccessControl, ReentrancyGuard, Pausable {
    using Counters for Counters.Counter;

    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // State variables
    Counters.Counter private _subscriptionIdCounter;

    // Subscription plans
    enum SubscriptionPlan {
        Free,     // 0 - Free tier
        Pro,      // 1 - Pro tier
        Premium   // 2 - Premium tier
    }

    // Subscription pricing (in wei)
    uint256 public constant PRO_MONTHLY_PRICE = 30 ether; // $30 in MATIC
    uint256 public constant PREMIUM_MONTHLY_PRICE = 50 ether; // $50 in MATIC
    uint256 public constant YEARLY_DISCOUNT_BPS = 2000; // 20% discount for yearly

    // Events
    event SubscriptionCreated(
        uint256 indexed subscriptionId,
        address indexed subscriber,
        SubscriptionPlan plan,
        uint256 duration,
        uint256 price,
        uint256 expiresAt
    );
    
    event SubscriptionRenewed(
        uint256 indexed subscriptionId,
        address indexed subscriber,
        uint256 newExpiresAt
    );
    
    event SubscriptionCancelled(
        uint256 indexed subscriptionId,
        address indexed subscriber
    );
    
    event PlanUpdated(
        SubscriptionPlan plan,
        uint256 monthlyPrice,
        uint256 yearlyPrice
    );

    // Structs
    struct Subscription {
        uint256 subscriptionId;
        address subscriber;
        SubscriptionPlan plan;
        uint256 createdAt;
        uint256 expiresAt;
        bool isActive;
        bool autoRenew;
        uint256 totalPaid;
    }

    // Mappings
    mapping(uint256 => Subscription) public subscriptions;
    mapping(address => uint256) public userActiveSubscription;
    mapping(address => uint256[]) public userSubscriptions;
    mapping(SubscriptionPlan => uint256) public planPrices;
    mapping(SubscriptionPlan => uint256) public planFeatures;

    // Modifiers
    modifier onlySubscriber(uint256 subscriptionId) {
        require(
            subscriptions[subscriptionId].subscriber == msg.sender,
            "Only subscriber can perform this action"
        );
        _;
    }

    modifier subscriptionExists(uint256 subscriptionId) {
        require(subscriptions[subscriptionId].subscriber != address(0), "Subscription does not exist");
        _;
    }

    modifier validPlan(SubscriptionPlan plan) {
        require(plan != SubscriptionPlan.Free, "Cannot subscribe to free plan");
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        
        // Initialize plan prices
        planPrices[SubscriptionPlan.Pro] = PRO_MONTHLY_PRICE;
        planPrices[SubscriptionPlan.Premium] = PREMIUM_MONTHLY_PRICE;
        
        // Initialize plan features (bitmap)
        planFeatures[SubscriptionPlan.Free] = 0; // No premium features
        planFeatures[SubscriptionPlan.Pro] = 1 | 2 | 4; // Priority listing, premium filters, analytics
        planFeatures[SubscriptionPlan.Premium] = 1 | 2 | 4 | 8 | 16; // All Pro features + advanced analytics, priority support
    }

    /**
     * @dev Create a new subscription
     * @param plan Subscription plan
     * @param duration Duration in months (1-12)
     * @param autoRenew Whether to auto-renew
     */
    function createSubscription(
        SubscriptionPlan plan,
        uint256 duration,
        bool autoRenew
    ) external 
        payable 
        nonReentrant 
        whenNotPaused 
        validPlan(plan) 
    {
        require(duration >= 1 && duration <= 12, "Duration must be between 1 and 12 months");
        require(userActiveSubscription[msg.sender] == 0, "User already has active subscription");
        
        uint256 price = calculatePrice(plan, duration);
        require(msg.value >= price, "Insufficient payment");

        uint256 subscriptionId = _subscriptionIdCounter.current();
        _subscriptionIdCounter.increment();

        uint256 expiresAt = block.timestamp + (duration * 30 days);

        Subscription memory newSubscription = Subscription({
            subscriptionId: subscriptionId,
            subscriber: msg.sender,
            plan: plan,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            isActive: true,
            autoRenew: autoRenew,
            totalPaid: price
        });

        subscriptions[subscriptionId] = newSubscription;
        userActiveSubscription[msg.sender] = subscriptionId;
        userSubscriptions[msg.sender].push(subscriptionId);

        emit SubscriptionCreated(subscriptionId, msg.sender, plan, duration, price, expiresAt);
    }

    /**
     * @dev Renew subscription
     * @param subscriptionId ID of the subscription
     * @param duration Additional duration in months
     */
    function renewSubscription(uint256 subscriptionId, uint256 duration) 
        external 
        payable 
        nonReentrant 
        onlySubscriber(subscriptionId) 
        subscriptionExists(subscriptionId) 
    {
        require(duration >= 1 && duration <= 12, "Duration must be between 1 and 12 months");
        require(subscriptions[subscriptionId].isActive, "Subscription is not active");
        
        uint256 price = calculatePrice(subscriptions[subscriptionId].plan, duration);
        require(msg.value >= price, "Insufficient payment");

        subscriptions[subscriptionId].expiresAt += duration * 30 days;
        subscriptions[subscriptionId].totalPaid += price;

        emit SubscriptionRenewed(subscriptionId, msg.sender, subscriptions[subscriptionId].expiresAt);
    }

    /**
     * @dev Cancel subscription
     * @param subscriptionId ID of the subscription
     */
    function cancelSubscription(uint256 subscriptionId) 
        external 
        onlySubscriber(subscriptionId) 
        subscriptionExists(subscriptionId) 
    {
        require(subscriptions[subscriptionId].isActive, "Subscription is not active");
        
        subscriptions[subscriptionId].isActive = false;
        subscriptions[subscriptionId].autoRenew = false;
        userActiveSubscription[msg.sender] = 0;

        emit SubscriptionCancelled(subscriptionId, msg.sender);
    }

    /**
     * @dev Toggle auto-renewal
     * @param subscriptionId ID of the subscription
     */
    function toggleAutoRenewal(uint256 subscriptionId) 
        external 
        onlySubscriber(subscriptionId) 
        subscriptionExists(subscriptionId) 
    {
        subscriptions[subscriptionId].autoRenew = !subscriptions[subscriptionId].autoRenew;
    }

    /**
     * @dev Process auto-renewal (admin only)
     * @param subscriptionId ID of the subscription
     */
    function processAutoRenewal(uint256 subscriptionId) 
        external 
        onlyRole(ADMIN_ROLE) 
        subscriptionExists(subscriptionId) 
    {
        require(subscriptions[subscriptionId].autoRenew, "Auto-renewal not enabled");
        require(subscriptions[subscriptionId].isActive, "Subscription is not active");
        require(block.timestamp >= subscriptions[subscriptionId].expiresAt, "Subscription not expired yet");

        // Extend subscription by 1 month
        subscriptions[subscriptionId].expiresAt += 30 days;
        
        emit SubscriptionRenewed(subscriptionId, subscriptions[subscriptionId].subscriber, subscriptions[subscriptionId].expiresAt);
    }

    /**
     * @dev Check if user has premium feature
     * @param user Address of the user
     * @param feature Feature bit (1, 2, 4, 8, 16, etc.)
     * @return Whether user has the feature
     */
    function hasFeature(address user, uint256 feature) external view returns (bool) {
        uint256 activeSubId = userActiveSubscription[user];
        if (activeSubId == 0) return false;
        
        Subscription memory sub = subscriptions[activeSubId];
        if (!sub.isActive || block.timestamp > sub.expiresAt) return false;
        
        return (planFeatures[sub.plan] & feature) != 0;
    }

    /**
     * @dev Check if user has active subscription
     * @param user Address of the user
     * @return Whether user has active subscription
     */
    function hasActiveSubscription(address user) external view returns (bool) {
        uint256 activeSubId = userActiveSubscription[user];
        if (activeSubId == 0) return false;
        
        Subscription memory sub = subscriptions[activeSubId];
        return sub.isActive && block.timestamp <= sub.expiresAt;
    }

    /**
     * @dev Get user's subscription plan
     * @param user Address of the user
     * @return Subscription plan
     */
    function getUserPlan(address user) external view returns (SubscriptionPlan) {
        uint256 activeSubId = userActiveSubscription[user];
        if (activeSubId == 0) return SubscriptionPlan.Free;
        
        Subscription memory sub = subscriptions[activeSubId];
        if (!sub.isActive || block.timestamp > sub.expiresAt) return SubscriptionPlan.Free;
        
        return sub.plan;
    }

    /**
     * @dev Calculate subscription price
     * @param plan Subscription plan
     * @param duration Duration in months
     * @return Price in wei
     */
    function calculatePrice(SubscriptionPlan plan, uint256 duration) public view returns (uint256) {
        uint256 monthlyPrice = planPrices[plan];
        uint256 totalPrice = monthlyPrice * duration;
        
        // Apply yearly discount for 12-month subscriptions
        if (duration == 12) {
            totalPrice = totalPrice - (totalPrice * YEARLY_DISCOUNT_BPS / 10000);
        }
        
        return totalPrice;
    }

    /**
     * @dev Get subscription details
     * @param subscriptionId ID of the subscription
     * @return Subscription struct
     */
    function getSubscription(uint256 subscriptionId) 
        external 
        view 
        subscriptionExists(subscriptionId) 
        returns (Subscription memory) 
    {
        return subscriptions[subscriptionId];
    }

    /**
     * @dev Get user subscriptions
     * @param user Address of the user
     * @return Array of subscription IDs
     */
    function getUserSubscriptions(address user) external view returns (uint256[] memory) {
        return userSubscriptions[user];
    }

    /**
     * @dev Update plan pricing (admin only)
     * @param plan Subscription plan
     * @param monthlyPrice New monthly price
     */
    function updatePlanPricing(SubscriptionPlan plan, uint256 monthlyPrice) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        planPrices[plan] = monthlyPrice;
        emit PlanUpdated(plan, monthlyPrice, calculatePrice(plan, 12));
    }

    /**
     * @dev Update plan features (admin only)
     * @param plan Subscription plan
     * @param features New features bitmap
     */
    function updatePlanFeatures(SubscriptionPlan plan, uint256 features) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        planFeatures[plan] = features;
    }

    /**
     * @dev Get plan features
     * @param plan Subscription plan
     * @return Features bitmap
     */
    function getPlanFeatures(SubscriptionPlan plan) external view returns (uint256) {
        return planFeatures[plan];
    }

    /**
     * @dev Get contract balance
     * @return Contract ETH balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Withdraw funds (admin only)
     * @param amount Amount to withdraw
     */
    function withdraw(uint256 amount) external onlyRole(ADMIN_ROLE) {
        require(amount <= address(this).balance, "Insufficient balance");
        payable(msg.sender).transfer(amount);
    }

    /**
     * @dev Pause contract (emergency only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    // Fallback function to receive ETH
    receive() external payable {}
}

